import PySimpleGUIQt as sg

menu_def = [['&Help', '&About...']]

             #, 'E&xit'  ],['&Help', '&About...'],]'''
menuLayout = [sg.Menu(menu_def, pad=(200, 1), key='menu', tearoff=True)]

layout = [
    [sg.Menu(menu_def, tearoff=False, pad=(200, 1))],
    ]

mainWindowIcon_32 = b''
mainWindowIcon_64 = b''

window = sg.Window('Tool Heading', layout, default_element_size=(100, 50),
                   finalize=True, location=(100, 0), resizable=True, icon=mainWindowIcon_64)

window.SetAlpha(1)
window.Refresh()

while True:
    event, value = window.read(timeout=100)
    #print(event, value)
    #print(event)

    # this would help us to make sure that we capture the close event when we press cross(X) from the main window
    # used with enable_close_attempted_event=True in main window call
    if event == '-WINDOW CLOSE ATTEMPTED-':
        break